import { findAllByDisplayValue } from "@testing-library/react";

export const initialState = {
  user: null,
  playlists: [],
  spotify: null,
  discover_weekly: null,
  top_artists: null,
  playing: false,
  item: null,
  token: 'BQCsDjxDm1j68LtZ9QNpHGouSXxqdBlljkLLjAg_0hc1z_g9IAv_GdAdgzUaDHs3CX7NNMbcpshXeAZ_thO5pcjvt5v-kkic5z_b2zYh-4oPJfEyy84cMzi0eRfGIbb9N6odvjFRfJaAiluNRcI6OVBS8NLrGWI0'
};

const reducer = (state, action) => {
    console.log(action);

    switch(action.type){
        case 'SET_USER':
            return{
                ...state,
                user: action.user,
            };
        case 'SET_TOKEN':
            return{
                ...state,
                token: action.token,

            };
        case 'SET_PLAYLISTS':
            return{
                ...state,
                playlists: action.pla
            }
        default:
            return state;
    }
}

export default reducer;
